import React, { Component, Fragment } from "react";

import "./AboutUs.scss";
import PageHeader from "../../components/PageHeader/PageHeader";

const AboutUs = () => {
  return (
    <Fragment>
      <PageHeader PageHeaderTitle={`אודותינו`} />
      <div className="section section-page-content">
        <div className="container">
          <div className="content">
            <div className="column">
              {/* <p className="column-item">נולדה מתוך ההבנה שהתהליך המוכר של השכרת דירות למגורים הוא ארוך ומתיש, מלווה בחששות וניירת רבה. המשכירים חוששים מחיבור עם גורם בעייתי שאינו מתאים להם, חווים קשיים רבים: לוגיסטיים, משפטיים ופיננסיים והפתרונות הקיימים אינם מתקדמים ואיכותיים מספיק.</p>
                                <p className="column-item">רוצה לשנות את הדרך בה אנשים משכירים דירות בישראל ובעולם, ע״י הפיכת החוויה לדיגיטלית, בטוחה ויעילה. מהיום הכל קורה במקום אחד, בלחיצת כפתור, בזמן אמת ובאחריות. לא עוד סרבול, בזבוז זמן וכאב ראש.</p> */}
              {/* <p>
                  DiffeRent היא פלטפורמה דיגיטלית מעולמות הפרופטק (Property Tech), המבטיחה לכם בטחון ושקט נפשי בכל הנוגע לשכר הדירה שלכם. היא נולדה מתוך המציאות של בעלי דירות רבים אשר גילו ששכר הדירה
                  רחוק מלהיות דבר מובטח. כשמשהו משתבש, היכולת של בעל הדירה לקבל את המגיע לו ולצאת מכך ללא נזק וללא בזבוז זמן מיותר, שואפת לאפס. DiffeRent משנה את הדרך בה אנשים משכירים דירות ע״י הפיכת
                  החוויה לדיגיטלית, יעילה והכי חשוב, בטוחה. מהיום הכל קורה במקום אחד, בלחיצת כפתור, בזמן אמת ובאחריות. לא עוד דאגה מתמדת ובזבוז זמן. בעבור רק 2.5% מדמי השכירות החודשיים, מקבל בעל הנכס
                  את הבטחת דמי השכירות, מציאת דייר חלופי במקרה הצורך ואף פינוי דייר סרבן, והכל בפשטות של לחיצת כפתור - Diffe-rent.co.il.
                </p> */}
              <p>
                DiffeRent היא פלטפורמה דיגיטלית מעולמות הפרופטק (Property Tech),
                המבטיחה לכם בטחון ושקט נפשי בכל הנוגע לשכר הדירה שלכם. היא נולדה
                מתוך המציאות של בעלי דירות רבים אשר גילו ששכר הדירה רחוק מלהיות
                דבר מובטח. כשמשהו משתבש, היכולת של בעל הדירה לקבל את המגיע לו
                ולצאת מכך ללא נזק וללא בזבוז זמן מיותר, שואפת לאפס. DiffeRent
                משנה את הדרך בה אנשים משכירים דירות ע״י הפיכת החוויה לדיגיטלית,
                יעילה והכי חשוב, בטוחה. מהיום הכל קורה במקום אחד, בלחיצת כפתור,
                בזמן אמת ובאחריות. לא עוד דאגה מתמדת ובזבוז זמן. בעבור רק 2.5%
                מדמי השכירות החודשיים, מקבל בעל הדירה את הבטחת דמי השכירות עד
                תום החוזה, פינוי דייר סרבן ומציאת דייר חלופי והכל בפשטות של
                לחיצת כפתור - Diffe-rent.co.il.
              </p>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default AboutUs;
